import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import ReactMarkdown from 'react-markdown';
import remarkGfm from 'remark-gfm';

const API_URL = 'http://localhost:5001/query';

// --- Interfaces ---
interface Source {
  content: string;
  file?: string;
}

interface Message {
  sender: 'user' | 'bot';
  text: string;
  sources?: Source[];
}

// --- SVG Icon Components ---
const SunIcon = ({ className }: { className?: string }) => (
    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><circle cx="12" cy="12" r="5"></circle><line x1="12" y1="1" x2="12" y2="3"></line><line x1="12" y1="21" x2="12" y2="23"></line><line x1="4.22" y1="4.22" x2="5.64" y2="5.64"></line><line x1="18.36" y1="18.36" x2="19.78" y2="19.78"></line><line x1="1" y1="12" x2="3" y2="12"></line><line x1="21" y1="12" x2="23" y2="12"></line><line x1="4.22" y1="19.78" x2="5.64" y2="18.36"></line><line x1="18.36" y1="5.64" x2="19.78" y2="4.22"></line></svg>
);

const MoonIcon = ({ className }: { className?: string }) => (
    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><path d="M21 12.79A9 9 0 1 1 11.21 3 7 7 0 0 0 21 12.79z"></path></svg>
);

const SendIcon = ({ className }: { className?: string }) => (
    <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg" className={className}><path d="M10 14L21 3" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/><path d="M21 3L14.5 21L10 14L3 10.5L21 3Z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"></path></svg>
);

const UserIcon = ({ className }: { className?: string }) => (
    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><path d="M20 21v-2a4 4 0 0 0-4-4H8a4 4 0 0 0-4 4v2"></path><circle cx="12" cy="7" r="4"></circle></svg>
);

const BotIcon = ({ className }: { className?: string }) => (
    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><path d="M12 8V4H8" /><rect x="4" y="12" width="16" height="8" rx="2" /><path d="M2 12h20" /><path d="m18 4-2.45 2.45" /><path d="m6 4 2.45 2.45" /></svg>
);


function App() {
    const [messages, setMessages] = useState<Message[]>([]);
    const [input, setInput] = useState<string>('');
    const [isLoading, setIsLoading] = useState<boolean>(false);
    const [theme, setTheme] = useState<'light' | 'dark'>('light');
    const chatEndRef = useRef<HTMLDivElement>(null);
    const textareaRef = useRef<HTMLTextAreaElement>(null);

    useEffect(() => {
        document.documentElement.className = theme;
    }, [theme]);
    
    useEffect(() => {
        chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    }, [messages, isLoading]);

    useEffect(() => {
        if (textareaRef.current) {
            textareaRef.current.style.height = 'auto';
            const scrollHeight = textareaRef.current.scrollHeight;
            const maxHeight = 200; // 200px max height
            textareaRef.current.style.height = `${Math.min(scrollHeight, maxHeight)}px`;
        }
    }, [input]);

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        if (!input.trim() || isLoading) return;

        const userMessage: Message = { sender: 'user', text: input };
        setMessages(prev => [...prev, userMessage]);
        setInput('');
        setIsLoading(true);

        try {
            const response = await axios.post<{ answer?: string; sources?: Source[] }>(API_URL, { query: input });
            const botMessage: Message = {
                sender: 'bot',
                text: response.data.answer || "Sorry, I couldn't get a response.",
                sources: response.data.sources || [],
            };
            setMessages(prev => [...prev, botMessage]);
        } catch (error) {
            const errorMessage: Message = { sender: 'bot', text: `An error occurred: ${(error as Error).message}` };
            setMessages(prev => [...prev, errorMessage]);
        } finally {
            setIsLoading(false);
        }
    };

    const handleKeyDown = (e: React.KeyboardEvent<HTMLTextAreaElement>) => {
        if (e.key === 'Enter' && !e.shiftKey) {
            e.preventDefault();
            handleSubmit(e as any);
        }
    }

    const toggleTheme = () => {
        setTheme(prevTheme => (prevTheme === 'light' ? 'dark' : 'light'));
    };

    return (
        <div className="bg-white dark:bg-gray-900 text-gray-900 dark:text-gray-100 flex flex-col h-screen font-sans relative overflow-hidden">
            {/* Decorative Elements - Pastel Flairs */}
            <div className="absolute top-0 -left-4 w-64 h-64 bg-rose-100 rounded-full mix-blend-multiply filter blur-xl opacity-40 animate-blob"></div>
            <div className="absolute top-20 left-32 w-48 h-48 bg-sky-100 rounded-full mix-blend-multiply filter blur-xl opacity-30 animate-blob animation-delay-2000"></div>
            <div className="absolute top-0 right-0 w-56 h-56 bg-violet-100 rounded-full mix-blend-multiply filter blur-xl opacity-40 animate-blob animation-delay-4000"></div>
            
            {/* Additional small decorative elements */}
            <div className="absolute bottom-40 right-20 w-32 h-32 bg-teal-100 rounded-full mix-blend-multiply filter blur-xl opacity-30 animate-blob"></div>
            <div className="absolute bottom-20 left-20 w-40 h-40 bg-pink-100 rounded-full mix-blend-multiply filter blur-xl opacity-30 animate-blob animation-delay-2000"></div>
            <div className="absolute top-1/2 left-1/2 w-36 h-36 bg-yellow-100 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-blob animation-delay-4000"></div>

            {/* Small accent dots */}
            <div className="absolute top-1/4 right-1/4 w-16 h-16 bg-purple-100 rounded-full mix-blend-multiply filter blur-lg opacity-40"></div>
            <div className="absolute bottom-1/3 right-1/3 w-20 h-20 bg-blue-100 rounded-full mix-blend-multiply filter blur-lg opacity-30"></div>

            <div className="relative flex flex-col h-full w-full max-w-4xl mx-auto z-10">
                {/* Header with enhanced glass effect */}
                <header className="sticky top-0 z-20 flex items-center justify-between p-4 bg-white/80 dark:bg-gray-800/80 backdrop-blur-md border-b border-gray-100">
                    <div className="flex items-center space-x-3">
                        <BotIcon className="w-8 h-8 text-violet-400" />
                        <h1 className="text-xl font-bold bg-gradient-to-r from-violet-500 to-purple-500 bg-clip-text text-transparent">RAG Assistant</h1>
                    </div>
                    <button onClick={toggleTheme} className="p-2 rounded-full hover:bg-gray-100/80 dark:hover:bg-gray-700/80 focus:outline-none focus:ring-2 focus:ring-violet-300">
                        {theme === 'light' ? <MoonIcon className="w-6 h-6" /> : <SunIcon className="w-6 h-6" />}
                    </button>
                </header>

                {/* Chat Area with softer shadows */}
                <main className="flex-1 overflow-y-auto p-6 space-y-8">
                    {messages.length === 0 && !isLoading ? (
                        <div className="text-center text-gray-500 dark:text-gray-400 pt-16">
                            <BotIcon className="w-16 h-16 mx-auto mb-4 text-violet-300" />
                            <h2 className="text-2xl font-semibold bg-gradient-to-r from-violet-500 to-purple-500 bg-clip-text text-transparent">Welcome to your RAG Assistant</h2>
                            <p className="text-gray-400">Ask a question about your documents to get started.</p>
                        </div>
                    ) : (
                        messages.map((msg, index) => (
                            <div key={index} className={`flex items-start gap-4 ${msg.sender === 'user' ? 'justify-end' : ''}`}>
                                {msg.sender === 'bot' && <BotIcon className="w-8 h-8 flex-shrink-0 text-violet-400" />}
                                <div className={`prose dark:prose-invert max-w-2xl p-4 rounded-2xl shadow-sm ${
                                    msg.sender === 'user' 
                                    ? 'bg-gradient-to-br from-violet-400 to-purple-400 text-white rounded-br-none' 
                                    : 'bg-white/80 dark:bg-gray-800/80 backdrop-blur-sm rounded-bl-none border border-gray-100'
                                }`}>
                                    <ReactMarkdown remarkPlugins={[remarkGfm]}>{msg.text}</ReactMarkdown>
                                </div>
                                {msg.sender === 'user' && (
                                    <div className="w-8 h-8 flex-shrink-0 bg-gradient-to-br from-violet-100 to-purple-100 rounded-full flex items-center justify-center shadow-sm">
                                        <UserIcon className="w-5 h-5 text-violet-500" />
                                    </div>
                                )}
                            </div>
                        ))
                    )}
                    {isLoading && (
                        <div className="flex items-start gap-4">
                            <BotIcon className="w-8 h-8 flex-shrink-0 text-violet-400" />
                            <div className="prose dark:prose-invert max-w-2xl p-4 rounded-2xl shadow-sm bg-white/80 dark:bg-gray-800/80 backdrop-blur-sm rounded-bl-none border border-gray-100">
                                <div className="flex items-center space-x-2">
                                    <div className="w-2 h-2 bg-violet-400 rounded-full animate-pulse"></div>
                                    <div className="w-2 h-2 bg-violet-400 rounded-full animate-pulse [animation-delay:0.2s]"></div>
                                    <div className="w-2 h-2 bg-violet-400 rounded-full animate-pulse [animation-delay:0.4s]"></div>
                                </div>
                            </div>
                        </div>
                    )}
                    <div ref={chatEndRef} />
                </main>

                {/* Footer with enhanced glass effect */}
                <footer className="sticky bottom-0 z-20 p-4 bg-gradient-to-t from-white to-white/60 dark:from-gray-900 dark:to-gray-900/60">
                    <form onSubmit={handleSubmit} className="relative max-w-3xl mx-auto">
                        <div className="relative">
                            <textarea
                                ref={textareaRef}
                                value={input}
                                onChange={(e) => setInput(e.target.value)}
                                onKeyDown={handleKeyDown}
                                placeholder="Ask your question..."
                                disabled={isLoading}
                                rows={1}
                                className="w-full p-4 pr-12 text-base bg-white/80 dark:bg-gray-800/80 border border-gray-200 rounded-2xl focus:outline-none focus:ring-2 focus:ring-violet-300 dark:focus:ring-violet-400 resize-none shadow-sm transition-all duration-300 backdrop-blur-md"
                            />
                            <button 
                                type="submit" 
                                className="absolute right-3 top-1/2 -translate-y-1/2 p-2 rounded-full text-violet-400 hover:bg-violet-50 dark:hover:bg-gray-700/80 disabled:text-gray-300 disabled:hover:bg-transparent transition-colors"
                                disabled={!input.trim() || isLoading}
                            >
                                <SendIcon className="w-6 h-6" />
                            </button>
                        </div>
                    </form>
                </footer>
            </div>
        </div>
    );
}

export default App;
